import React, { useState } from "react";
import axios from "axios";

function App() {
  const [pdfFile, setPdfFile] = useState(null);
  const [filename, setFilename] = useState("");
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");

  const uploadPDF = async () => {
    if (!pdfFile) return alert("Please select a PDF file first.");
    const formData = new FormData();
    formData.append("file", pdfFile);

    try {
      const res = await axios.post("http://localhost:8000/upload", formData);
      setFilename(res.data.filename);
      alert(res.data.message);
    } catch (err) {
      console.error("Upload error:", err);
      alert("Upload failed.");
    }
  };

  const askQuestion = async () => {
    if (!filename || !question) return alert("Upload a PDF and ask a question.");
    const formData = new FormData();
    formData.append("filename", filename);
    formData.append("question", question);

    try {
      const res = await axios.post("http://localhost:8000/ask", formData);
      setAnswer(res.data.answer);
    } catch (err) {
      console.error("Ask error:", err);
      alert("Failed to get answer.");
    }
  };

  return (
    <div className="container py-5">
      <h1 className="text-center mb-4">ðŸ“„ PDF Q&A Chatbot</h1>

      <div className="mb-4">
        <label className="form-label">Upload PDF:</label>
        <input
          type="file"
          className="form-control"
          onChange={(e) => setPdfFile(e.target.files[0])}
        />
        <button className="btn btn-primary mt-2" onClick={uploadPDF}>
          Upload
        </button>
      </div>

      <div className="mb-4">
        <label className="form-label">Ask a Question:</label>
        <input
          type="text"
          className="form-control"
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          placeholder="Type your question here..."
        />
        <button className="btn btn-success mt-2" onClick={askQuestion}>
          Ask
        </button>
      </div>

      {answer && (
        <div className="alert alert-info">
          <strong>Answer:</strong> {answer}
        </div>
      )}
    </div>
  );
}

export default App;
